example.code:
var array = [32, 8, 2, 16, 4];

var compare = function (a, b) {
	if (a < b) {
		return -1;
	}
	else if (a > b) {
		return 1;
	}
	return 0;
};

console.log(array);                 // [32, 8, 2, 16, 4]
console.log(array.sort(compare));   // [2, 4, 8, 16, 32]
console.log(array);                 // [2, 4, 8, 16, 32]

var array = [32, 8, 2, 16, 4];

console.log(array);                 // [32, 8, 2, 16, 4]
console.log(array.sort(function (a, b) {
	return a - b;
}));                                // [2, 4, 8, 16, 32]
console.log(array);                 // [2, 4, 8, 16, 32]
