example.code:
var array = [2, 4, 8];

var result = array.find(function () {
	console.log(this);
	return true;
});

console.log(result);  // 2

var arr = [2, 4, 8];

var result = arr.find(function (value, index, array) {
	console.log(value, index, array);
	return true;
});

console.log(result);  // 2

var array = [2, 4, 8];

var result = array.find(function () {
	console.log(this);
	return true;
}, document.location);

console.log(result);  // 2

var array = [2, 4, 4, 8];

var result = array.find(function (value) {
	if (value === 2) {
		return false;
	}
	return true;
});

console.log(result);  // 4

var array = [2, 4, 8];

var result = array.find(function (value) {
	console.log(value);
	return false;
});

console.log(result);  // undefined
