example.code:
var array = [2, 4, 8];

var result = array.findIndex(function () {
	console.log(this);
	return true;
});

console.log(result);  // 0

var arr = [2, 4, 8];

var result = arr.findIndex(function (value, index, array) {
	console.log(value, index, array);
	return true;
});

console.log(result);  // 0

var array = [2, 4, 8];

var result = array.findIndex(function () {
	console.log(this);
	return true;
}, document.location);

console.log(result);  // 0

var array = [2, 4, 4, 8];

var result = array.findIndex(function (value) {
	if (value === 2) {
		return false;
	}
	return true;
});

console.log(result);  // 1

var array = [2, 4, 8];

var result = array.findIndex(function (value) {
	console.log(value);
	return false;
});

console.log(result);  // -1
