example.code:
var array = [2, 4, 8];

var result = array.every(function () {
	console.log(this);
	return true;
});

console.log(result);  // true

var arr = [2, 4, 8];

var result = arr.every(function (value, index, array) {
	console.log(value, index, array);
	return true;
});

console.log(result);  // true

var array = [2, 4, 8];

var result = array.every(function () {
	console.log(this);
	return true;
}, document.location);

console.log(result);  // true

var array = [2, 4, 8];

var result = array.every(function (value) {
	console.log(this);
	if (value === 4) {
		return 0;
	}
	return true;
});

console.log(result);  // false
