example.code:
var b = new Boolean(true); // Create a new boolean object.
if (b.valueOf()) {
	console.log('The value of b is true.');
}

var b = true; // Create a new boolean primitive.
if (b) {
	console.log('The value of b is true.');
}

bool = Boolean('false');
console.log(bool); // Will return true.

bool = Boolean(0);
console.log(bool); // Will return false.
