example.code:
let mypromise = new Promise((resolve, reject) => {
	reject('rejected :(');
});

mypromise.catch(result => {
	console.log(result);
});

let mypromise = new Promise((resolve, reject) => {
	reject('rejected :(');
});

// Nothing to catch the error, so an error message is shown.

// After about 2 seconds the catch method is attached and replaces the previous error.
setTimeout(() => {
	mypromise.catch(result => {
		console.log(result);
	});
}, 2000);
