example.code:
new Promise((resolve, reject) => {
	// Randomly resolve or reject the promise.
	if (Math.random() >= 0.5) {
		resolve('resolved! :)');
	}
	reject('rejected :(');
}).then(result => {
	console.log(result);
}).catch(error => {
	console.log(error);
}).finally(() => {
	console.log('Finally!');
});
