example.code:
regex = /^a/g;
console.log(regex.lastIndex, regex.test("abc"), regex.lastIndex); // true, lastIndex was 0
console.log(regex.lastIndex, regex.test("abc"), regex.lastIndex); // false, lastIndex was 1

regex = /^a/g;
console.log(regex.lastIndex, regex.test("abc"), regex.lastIndex); // true, lastIndex was 0
regex.lastIndex = 0;
console.log(regex.lastIndex, regex.test("abc"), regex.lastIndex); // true, lastIndex was 0
