example.code:
function f (a, b) {
	return a + b;
}

var result = f(1, 2);
console.log(result);  // 3

function f () {
	return;
}

var result = f();
console.log(result);  // undefined

var val = 'initial value';

function f (a) {
	if (a === undefined) {
		return;
	}
	val = a;
}

console.log(val);  // initial value

f();
console.log(val);  // initial value

f('changed value');
console.log(val);  // changed value
