example.code:
var expr = "Hello";
switch (expr) {
	case "Hello":
		console.log('expr was set to "Hello"');
		break;
	case "World":
		console.log('expr was set to "Wrold"');
		break;
	case "foo":
	case "bar":
		console.log('expr was set to "foo" or "bar"');
		break;
	default:
		console.log('The value of expr was not matched.');
}
