example.code:
console.log('Before section label');
section: {
	break section;
	console.log('Never executed');
}
console.log('After section label');

section: {
	console.log('In section');
	subSection: {
		console.log('In sub section');
		break section;
		console.log('Never executed');
	}
	console.log('Never executed');
}
console.log('It can even be nested!');
