example.code:
try {
	console.log(abc);
}
catch (e) {
	console.log(e);  // ReferenceError: abc is not defined(…)
}

try {
	console.log(abc);
}
catch (e) {
	console.log(e);  // ReferenceError: abc is not defined(…)
}
finally {
	console.log('Finally.');  // Finally will be run regardless of errors caught.
}
