example.code:
var timeout = setTimeout(function () {
	console.log('Will never be executed.');
}, 100);
clearTimeout(timeout);

var timeout;
var loop = function () {
	timeout = setTimeout(function () {
		console.log('Recursivle executes until cleared.');
		loop();
	}, 200);
};
setTimeout(function () {
	console.log('Clearing recursive timeout.');
	clearTimeout(timeout);
}, 1500);
loop();
